var DoH = Enumerable.Range(8, 7);
var gearSets = GearsetManager.GearSets.Where(i => i.InUse && DoH.Contains((int)i.Class) && Core.Player.Levels[i.Class] < 67);
if (gearSets.Any())
{
	gearSets.First().Activate();
