foreach (var plugin in PluginManager.Plugins.Where(p => p.Plugin.Name == "Enable Flight" || p.Plugin.Name == "启用飞行"))
{
  try
  {
    if (plugin.Enabled)  {  break;  }  else  {  plugin.Enabled = true;  }
  }
  catch (Exception ex) {  }
}


foreach (var plugin in PluginManager.Plugins.Where(p => p.Plugin.Name == "Enable Flight" || p.Plugin.Name == "启用飞行"))
{
  try
  {
    if (!plugin.Enabled)  {  break;  }  else  {  plugin.Enabled = false;  }
  }
  catch (Exception ex) {  }
}
